-- Create "contents" table
CREATE TABLE "contents" (
  "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "type" character varying NOT NULL,
  "icon_image" character varying NOT NULL,
  "cover_image" character varying NOT NULL,
  "title" character varying NOT NULL,
  "subtitle" character varying NOT NULL,
  "description" character varying NOT NULL,
  "source" character varying NOT NULL,
  "source_url" character varying NOT NULL,
  "licence" character varying NOT NULL,
  "licence_url" character varying NOT NULL,
  "is_hidden" boolean NOT NULL DEFAULT false,
  "order" bigint NOT NULL,
  "episodes_count" bigint NOT NULL,
  PRIMARY KEY ("id")
);
-- Create "content_episodes" table
CREATE TABLE "content_episodes" (
  "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "type" character varying NOT NULL,
  "title" character varying NOT NULL,
  "subtitle" character varying NOT NULL,
  "description" character varying NOT NULL,
  "duration" bigint NOT NULL,
  "stream_url" character varying NOT NULL,
  "download_url" character varying NOT NULL,
  "source" character varying NOT NULL DEFAULT '',
  "source_url" character varying NOT NULL DEFAULT '',
  "licence" character varying NOT NULL DEFAULT '',
  "licence_url" character varying NOT NULL DEFAULT '',
  "is_hidden" boolean NOT NULL DEFAULT false,
  "order" bigint NOT NULL,
  "content_id" bigint NOT NULL,
  PRIMARY KEY ("id"),
  CONSTRAINT "content_episodes_contents_content" FOREIGN KEY ("content_id") REFERENCES "contents" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION
);
-- Create "users" table
CREATE TABLE "users" (
  "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "firebase_uid" character varying NOT NULL,
  "display_name" character varying NOT NULL,
  "created_at" timestamptz NOT NULL,
  "updated_at" timestamptz NOT NULL,
  PRIMARY KEY ("id")
);
-- Create index "users_firebase_uid_key" to table: "users"
CREATE UNIQUE INDEX "users_firebase_uid_key" ON "users" ("firebase_uid");
-- Create "content_progresses" table
CREATE TABLE "content_progresses" (
  "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "content_type" character varying NOT NULL,
  "progress" bigint NOT NULL DEFAULT 0,
  "finished_at" timestamptz NULL,
  "created_at" timestamptz NOT NULL,
  "updated_at" timestamptz NOT NULL,
  "user_id" bigint NOT NULL,
  "content_id" bigint NOT NULL,
  "episode_id" bigint NOT NULL,
  PRIMARY KEY ("id"),
  CONSTRAINT "content_progresses_content_episodes_episode" FOREIGN KEY ("episode_id") REFERENCES "content_episodes" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION,
  CONSTRAINT "content_progresses_contents_content" FOREIGN KEY ("content_id") REFERENCES "contents" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION,
  CONSTRAINT "content_progresses_users_user" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION
);
-- Create "courses" table
CREATE TABLE "courses" (
  "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "icon_image" character varying NOT NULL,
  "cover_image" character varying NOT NULL,
  "title" character varying NOT NULL,
  "description" character varying NOT NULL,
  "color" character varying NOT NULL,
  "active" boolean NOT NULL,
  "order" bigint NOT NULL,
  PRIMARY KEY ("id")
);
-- Create "units" table
CREATE TABLE "units" (
  "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "icon_image" character varying NOT NULL,
  "cover_image" character varying NOT NULL,
  "title" character varying NOT NULL,
  "subtitle" character varying NOT NULL,
  "description" character varying NOT NULL,
  "mastering" character varying NOT NULL,
  "days" bigint NOT NULL,
  "color" character varying NOT NULL,
  "order" bigint NOT NULL,
  "course_id" bigint NOT NULL,
  PRIMARY KEY ("id"),
  CONSTRAINT "units_courses_course" FOREIGN KEY ("course_id") REFERENCES "courses" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION
);
-- Create "lessons" table
CREATE TABLE "lessons" (
  "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "type" character varying NOT NULL,
  "icon_image" character varying NOT NULL,
  "cover_image" character varying NOT NULL,
  "title" character varying NOT NULL,
  "description" character varying NOT NULL,
  "order" bigint NOT NULL,
  "unit_id" bigint NOT NULL,
  PRIMARY KEY ("id"),
  CONSTRAINT "lessons_units_unit" FOREIGN KEY ("unit_id") REFERENCES "units" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION
);
-- Create "journal_records" table
CREATE TABLE "journal_records" (
  "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "date_time" timestamptz NOT NULL,
  "duration" bigint NOT NULL,
  "note" character varying NOT NULL,
  "created_at" timestamptz NOT NULL,
  "updated_at" timestamptz NOT NULL,
  "user_id" bigint NOT NULL,
  "lesson_id" bigint NULL,
  PRIMARY KEY ("id"),
  CONSTRAINT "journal_records_lessons_lesson" FOREIGN KEY ("lesson_id") REFERENCES "lessons" ("id") ON UPDATE NO ACTION ON DELETE SET NULL,
  CONSTRAINT "journal_records_users_user" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION
);
-- Create "lesson_audios" table
CREATE TABLE "lesson_audios" (
  "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "audio_url" character varying NOT NULL,
  "duration" bigint NOT NULL,
  "lesson_id" bigint NOT NULL,
  PRIMARY KEY ("id"),
  CONSTRAINT "lesson_audios_lessons_lesson" FOREIGN KEY ("lesson_id") REFERENCES "lessons" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION
);
-- Create "techniques" table
CREATE TABLE "techniques" (
  "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "icon_image" character varying NOT NULL,
  "cover_image" character varying NOT NULL,
  "title" character varying NOT NULL,
  "subtitle" character varying NOT NULL,
  "description" character varying NOT NULL,
  "order" bigint NOT NULL,
  "unit_id" bigint NOT NULL,
  PRIMARY KEY ("id"),
  CONSTRAINT "techniques_units_unit" FOREIGN KEY ("unit_id") REFERENCES "units" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION
);
-- Create "lesson_techniques" table
CREATE TABLE "lesson_techniques" (
  "order" bigint NOT NULL,
  "lesson_id" bigint NOT NULL,
  "technique_id" bigint NOT NULL,
  PRIMARY KEY ("lesson_id", "technique_id"),
  CONSTRAINT "lesson_techniques_lessons_lesson" FOREIGN KEY ("lesson_id") REFERENCES "lessons" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION,
  CONSTRAINT "lesson_techniques_techniques_technique" FOREIGN KEY ("technique_id") REFERENCES "techniques" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION
);
-- Create "unit_techniques" table
CREATE TABLE "unit_techniques" (
  "position" bigint NOT NULL,
  "unit_id" bigint NOT NULL,
  "technique_id" bigint NOT NULL,
  PRIMARY KEY ("unit_id", "technique_id"),
  CONSTRAINT "unit_techniques_techniques_technique" FOREIGN KEY ("technique_id") REFERENCES "techniques" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION,
  CONSTRAINT "unit_techniques_units_unit" FOREIGN KEY ("unit_id") REFERENCES "units" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION
);
-- Create "user_progresses" table
CREATE TABLE "user_progresses" (
  "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "lesson_type" character varying NOT NULL,
  "is_last" boolean NOT NULL,
  "is_next" boolean NOT NULL,
  "progress" bigint NOT NULL DEFAULT 0,
  "finished_at" timestamptz NULL,
  "created_at" timestamptz NOT NULL,
  "updated_at" timestamptz NOT NULL,
  "user_id" bigint NOT NULL,
  "course_id" bigint NOT NULL,
  "unit_id" bigint NOT NULL,
  "lesson_id" bigint NOT NULL,
  PRIMARY KEY ("id"),
  CONSTRAINT "user_progresses_courses_course" FOREIGN KEY ("course_id") REFERENCES "courses" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION,
  CONSTRAINT "user_progresses_lessons_lesson" FOREIGN KEY ("lesson_id") REFERENCES "lessons" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION,
  CONSTRAINT "user_progresses_units_unit" FOREIGN KEY ("unit_id") REFERENCES "units" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION,
  CONSTRAINT "user_progresses_users_user" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION
);
